import { createRouter, createWebHistory } from 'vue-router';
import DashboardPage from '../views/DashboardPage.vue';
import SignupPage from '../components/SignupPage.vue'; 
import LoginPage from '../components/LoginPage.vue';
import MilestoneForm from '../components/MilestoneForm.vue';
import MilestoneList from '../components/MilestoneList.vue';
import ChildDevelopmentList from '../components/ChildDevelopmentList.vue';
import { useAuthStore } from '../stores/authStore';

const routes = [
  { path: '/', component: SignupPage, meta: { requiresAuth: false } },
  { path: '/login', component: LoginPage, meta: { requiresAuth: false } },
  { path: '/dashboard', component: DashboardPage, meta: { requiresAuth: true } },
  { path: '/milestone-form', component: MilestoneForm, meta: { requiresAuth: true } },
  { path: '/milestone-list', component: MilestoneList, meta: { requiresAuth: true } },
  { path: '/child-development-list', component: ChildDevelopmentList, meta: { requiresAuth: true } }
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

// Provera pre nego što se ruta učita
router.beforeEach((to, from, next) => {
  const authStore = useAuthStore();
  authStore.checkAuth(); // Proveri stanje autentifikacije

  if (to.matched.some(record => record.meta.requiresAuth) && !authStore.isAuthenticated) {
    next('/login'); // Preusmeri na login ako korisnik nije ulogovan
  } else {
    next(); // Ako je korisnik ulogovan, dozvoli pristup ruti
  }
});

export default router;
