import { createRouter, createWebHistory } from 'vue-router';

import DashboardPage from '../views/DashboardPage.vue';
import SignupPage from '../components/SignupPage.vue'; 
import LoginPage from '../components/LoginPage.vue'; // Dodajemo LoginPage import
import ChildDevelopmentForm from '../components/ChildDevelopmentForm.vue';
import ChildDevelopmentList from '../components/ChildDevelopmentList.vue';
import MilestoneForm from '../components/MilestoneForm.vue';
import MilestoneList from '../components/MilestoneList.vue'; // Ovaj import je sada jedinstven

const routes = [
  { 
    path: '/', 
    component: LoginPage, 
    meta: { requiresAuth: false } 
  },
  { 
    path: '/dashboard', 
    component: DashboardPage, 
    meta: { requiresAuth: true } 
  },
  { 
    path: '/signup', 
    component: SignupPage 
  },
  { 
    path: '/login', 
    component: LoginPage, 
    meta: { requiresAuth: false } 
  },
  { 
    path: '/child-development', 
    component: ChildDevelopmentForm 
  },
  { 
    path: '/child-development-list', 
    component: ChildDevelopmentList 
  },
  { 
    path: '/milestone-form', 
    component: MilestoneForm 
  },
  { 
    path: '/milestone-list', 
    component: MilestoneList 
  }
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

// Provera pre nego što se ruta učita
router.beforeEach((to, from, next) => {
  const token = localStorage.getItem("token");

  // Ako ruta zahteva autentifikaciju, a korisnik nije prijavljen, preusmeri na login
  if (to.matched.some(record => record.meta.requiresAuth) && !token) {
    next('/login'); 
  } else {
    next(); 
  }
});

export default router;
