import { defineStore } from 'pinia';

export const useAuthStore = defineStore('auth', {
  state: () => ({
    isAuthenticated: false,
    childIds: [], // Store all child IDs for the user
    currentChildId: null, // Store the currently selected child's ID
  }),

  actions: {
    login(token, childId) {
      this.isAuthenticated = true;
      sessionStorage.setItem('token', token);
      sessionStorage.setItem('childIds', JSON.stringify([childId])); // For a single child, store it in an array
      sessionStorage.setItem('currentChildId', childId); // Set current childId
      this.childIds = [childId];
      this.currentChildId = childId;
    },

    logout() {
      this.isAuthenticated = false;
      this.childIds = [];
      this.currentChildId = null;
      sessionStorage.removeItem('token');
      sessionStorage.removeItem('childIds');
      sessionStorage.removeItem('currentChildId');
    },

    checkAuth() {
      const token = sessionStorage.getItem('token');
      this.isAuthenticated = !!token;
      const storedChildIds = JSON.parse(sessionStorage.getItem('childIds')) || [];
      const storedCurrentChildId = sessionStorage.getItem('currentChildId');
      this.childIds = storedChildIds;
      this.currentChildId = storedCurrentChildId || null;
    },

    addChildId(childId) {
      if (!this.childIds.includes(childId)) {
        this.childIds.push(childId);
        sessionStorage.setItem('childIds', JSON.stringify(this.childIds));
      }
      this.setCurrentChildId(childId); // Switch to the new child
    },

    setCurrentChildId(childId) {
      this.currentChildId = childId;
      sessionStorage.setItem('currentChildId', childId);
    },

    switchChild(childId) {
      this.setCurrentChildId(childId);
    }
  }
});
