import { createRouter, createWebHistory } from 'vue-router';
import HomePage from '../views/HomePage.vue';  // Možeš zadržati HomePage, ali neće biti početna stranica
import DashboardPage from '../views/DashboardPage.vue';
import LoginPage from '../components/LoginPage.vue';  // Importovanje LoginPage
import ChildDevelopmentForm from '../components/ChildDevelopmentForm.vue';
import ChildDevelopmentList from '../components/ChildDevelopmentList.vue';
import MilestoneForm from '../components/MilestoneForm.vue';
import MilestoneList from '../components/MilestoneList.vue';

const routes = [
  { 
    path: '/', 
    component: LoginPage, 
    meta: { requiresAuth: false } 
  },
  { 
    path: '/dashboard', 
    component: DashboardPage, 
    meta: { requiresAuth: true } 
  },
  { 
    path: '/login', 
    component: LoginPage, 
    meta: { requiresAuth: false } 
  },
  { path: '/child-development', component: ChildDevelopmentForm },
  { path: '/child-development-list', component: ChildDevelopmentList },
  { path: '/milestone-form', component: MilestoneForm },
  { path: '/milestone-list', component: MilestoneList }
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

router.beforeEach((to, from, next) => {
  const token = localStorage.getItem("token");

  
  if (to.matched.some(record => record.meta.requiresAuth) && !token) {
    next('/login'); 
  } else {
    next(); 
  }
});

export default router;
