import "core-js/modules/es.array.push.js";
import axios from 'axios';
export default {
  data() {
    return {
      newChild: {
        name: '',
        age: '',
        developmentStage: ''
      },
      errorMessage: ''
    };
  },
  methods: {
    async submitChildForm() {
      // Prvo proverite da li je korisnik ulogovan
      const token = localStorage.getItem('token');
      if (!token) {
        this.errorMessage = 'Morate biti ulogovani da biste dodali dete.';
        return;
      }

      // Ako polja nisu popunjena, prikažite grešku
      if (!this.newChild.name || !this.newChild.age || !this.newChild.developmentStage) {
        this.errorMessage = 'Sva polja su obavezna.';
        return;
      }
      try {
        // Dodajte token u Authorization header
        const response = await axios.post('http://localhost:5000/api/children', this.newChild, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        console.log('Podaci su uspešno poslati:', response);

        // Preusmerite korisnika na child-development
        this.$router.push('/child-development');
      } catch (error) {
        console.error('Greška pri registraciji deteta:', error);
        this.errorMessage = 'Greška prilikom registracije deteta. Molimo pokušajte ponovo.';
      }
    }
  }
};
