import { createRouter, createWebHistory } from 'vue-router';
import DashboardPage from '../views/DashboardPage.vue';
import SignupPage from '../components/SignupPage.vue'; 
import LoginPage from '../components/LoginPage.vue';
import ChildDevelopmentForm from '../components/ChildDevelopmentForm.vue';
import ChildDevelopmentList from '../components/ChildDevelopmentList.vue';
import MilestoneForm from '../components/MilestoneForm.vue';
import MilestoneList from '../components/MilestoneList.vue';
import ChildDevelopmentPage from '@/views/ChildDevelopmentPage.vue';

const routes = [
  { 
    path: '/', 
    component: SignupPage, 
    meta: { requiresAuth: false } // Signup stranica nije zaštićena
  },
  { 
    path: '/login', 
    component: LoginPage, 
    meta: { requiresAuth: false }  // Login stranica nije zaštićena
  },
  { 
    path: '/dashboard', 
    component: DashboardPage, 
    meta: { requiresAuth: true }  // Zaštita za Dashboard
  },
  { 
    path: '/child-development', 
    component: ChildDevelopmentForm, 
    meta: { requiresAuth: true }  // Zaštićena ruta
  },
  { 
    path: '/child-development-list', 
    component: ChildDevelopmentList, 
    meta: { requiresAuth: true }  // Zaštićena ruta
  },
  { 
    path: '/milestone-form', 
    component: MilestoneForm, 
    meta: { requiresAuth: true }  // Zaštićena ruta
  },
  { 
    path: '/milestone-list', 
    component: MilestoneList, 
    meta: { requiresAuth: true }  // Zaštićena ruta
  },
  { 
    path: '/child-development/:childId/:startAge', 
    component: ChildDevelopmentPage, 
    props: true, 
    meta: { requiresAuth: true }  // Zaštićena ruta
  },
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

// Preusmeravanje korisnika na login stranicu ako pokušava da pristupi zaštićenoj ruti bez prijave
router.beforeEach((to, from, next) => {
  const token = localStorage.getItem("token");

  if (to.matched.some(record => record.meta.requiresAuth) && !token) {
    next('/login'); 
  } else {
    next(); 
  }
});

export default router;
