import { defineStore } from 'pinia';

export const useAuthStore = defineStore('auth', {
  state: () => ({
    isAuthenticated: false,  // Početno stanje je 'false' dok korisnik nije prijavljen
  }),
  actions: {
    login() {
      this.isAuthenticated = true;
      localStorage.setItem('token', 'some-token'); // Dodajte validan token ovde
      console.log("Korisnik je prijavljen.");
    },
    logout() {
      this.isAuthenticated = false;
      localStorage.removeItem('token');  // Uklanjanje tokena
      console.log("Korisnik je odjavljen.");
    },
    checkAuth() {
      this.isAuthenticated = !!localStorage.getItem('token');
      console.log("CheckAuth:", this.isAuthenticated);
    }
  }
});
